<script>
    export default {
        name: 'IconLogo',

        data() {
            return {
                isHovered: false,
                isWordFilled: false,

                offset: 0,
                timer: null,

                // Animation options
                duration: .4,
            };
        },

        watch: {
            offset(val) {
                this.isWordFilled = false;

                if (val === 0) {
                    clearInterval(this.timer);
                    return;
                }
                if (val === 50) {
                    return;
                }
                if (val === 100) {
                    clearInterval(this.timer);
                    this.isWordFilled = true;
                }
            },
        },

        methods: {
            handleHover(val) {
                this.isHovered = Boolean(val);

                if (val) {
                    clearInterval(this.timer);
                    this.timer = setInterval(() => {
                        this.offset += 1;
                    }, this.duration * 10);
                }

                if (!val) {
                    clearInterval(this.timer);
                    this.timer = setInterval(() => {
                        this.offset -= 1;
                    }, this.duration * 10);
                }
            },
        },
    };
</script>

<style lang="scss">
    .ida {
        position: relative;
        width: 6rem;
        height: 2.8rem;
        cursor: pointer;

        &__logo {
            position: absolute;
            bottom: 0;
            left: 0;
            width: 100%;
            fill: $base-200;
            transform: translate3d(0, 0, 0);
            transition: opacity .3s linear;
        }

        .stop {
            stop-color: $base-200;
        }

        .stop-fill {
            stop-color: $blue;
        }
    }
</style>
